mapper.insert:
INSERT INTO
COMMENT_INFO (
CM_NUM
,BOARD_NUM
,CM_CONTENT
,CM_USER_NUM
,CM_RECOMMENT_NUM
)
VALUES (
COMMENT_SEQ.nextVal
,#{boardNum}
,#{content}
,#{userNum}
,NVL(#{recommentNum},'')
)

mapper.update:
update board_info SET
board_title = #{board_title},
board_content = #{board_content},
board_img = #{board_img}
where board_num = #{board_num}
